package test.liuzehua.bootrabbitmq.rabbitmq;import org.springframework.amqp.rabbit.annotation.Queue;import org.springframework.amqp.rabbit.annotation.RabbitHandler;import org.springframework.amqp.rabbit.annotation.RabbitListener;import org.springframework.stereotype.Component;/** * @author liuzehua * 2020/6/7 **/@Component@RabbitListener(queuesToDeclare = @Queue(value = "template" ,autoDelete = "true"))public class RabbitmqTest {    @RabbitHandler    public void receive(String message){        System.err.println("消费者收到的消息：：：："+message);    }}