package test.liuzehua.rabbitmqdemo.direct;import com.rabbitmq.client.Channel;import com.rabbitmq.client.Connection;import test.liuzehua.rabbitmqdemo.utils.ConnectionFactoryUtil;import java.io.IOException;/** * @author liuzehua * 2020/6/6 **/public class Provider {    public static void main(String[] args) throws IOException {        Connection connection = ConnectionFactoryUtil.getConnection();        Channel channel = connection.createChannel();        channel.exchangeDeclare("logs","direct");        String key = "info,error,debug";        String[] split = key.split(",");        for (int i = 0; i < split.length; i++) {            String s = split[i];            channel.basicPublish("logs",s,null,("direct  type--"+s).getBytes());        }        ConnectionFactoryUtil.close(channel,connection);    }}